class AbstractContainer:
    """Dummy class
    sets every (kw)argument in init to self
    """
    def __init__(self, **kwargs):
        for key, value in kwargs.items():
            setattr(self, key, value)


class Book(AbstractContainer):
    """Book"""
    title: str
    subtitle: str
    publishers: list
    pagination: str
    identifiers: list
    cover: dict
    subjects: list
    authors: list
    classifications: dict
    number_of_pages: int
    publish_date: int
    publish_places: list
    key: any
    url: str

    def __str__(self):
        return self.title


class Author(AbstractContainer):
    """Author"""
    name: str
    url: str

    def __str__(self):
        return self.name


class Publisher(AbstractContainer):
    """Publisher"""
    name: str

    def __str__(self):
        return self.name
